  const { id } = useParams();
  const moveTo = useNavigate();
  const [trackingInfo, setTrackingInfo] = useState([]);
  const [itemInfo, setItemInfo] = useState([]);

  // geting  user_tracking_Id
  const sendRequest = async () => {
    const res = await axios
      .get(
        `https://eplcourierservice-backend.vercel.app/api/v1/user_tracking_Id/${id}`
      )
      .catch((err) => {
        moveTo("/");
        errorHandle(err.response?.data);
        console.log(err, err?.response?.data);
      });

    if (res) {
      const data = await res?.data;
      console.log(data);
      return data;
    }
  };
  // geting  user_tracking_Item
  const sendRequestItem = async () => {
    const res = await axios
      .get(
        `https://eplcourierservice-backend.vercel.app/api/v1/item_tracking_Id/${id}`
      )
      .catch((err) => {
        console.log(err, err?.response?.data);
      });

    if (res) {
      const data = await res?.data;
      console.log(data);
      return data;
    }
  };
